import pkg from 'whatsapp-web.js';
const { Client, LocalAuth, MessageMedia } = pkg as any;
import QRCode from 'qrcode';
import qrImage from 'qr-image';
import { storage } from '../storage';
import fs from 'fs';
import path from 'path';
import type { WebSocket } from 'ws';

export class WhatsAppService {
  private client: any = null;
  private qrCode: string | null = null;
  private sessionInfo: any = null;
  private isReady: boolean = false;
  private isInitializing: boolean = false;
  private messageCache: Map<string, any[]> = new Map(); // Cache for real-time messages
  private connectionCheckInterval: NodeJS.Timeout | null = null;
  private currentState: string = 'DISCONNECTED';
  private isPhoneConnected: boolean = false;
  private lastStateCheck: Date = new Date();

  constructor() {
    this.initializeClient();
    this.startConnectionMonitoring();
  }

  // Helper method to broadcast WebSocket events
  private broadcastToClients(eventType: string, data: any) {
    try {
      const wss = (global as any).wss;
      if (wss && wss.clients) {
        const message = JSON.stringify({ type: eventType, data });
        wss.clients.forEach((client: any) => {
          if (client.readyState === 1) { // WebSocket.OPEN
            client.send(message);
          }
        });
        console.log(`ðŸ“¡ Broadcasted ${eventType} to ${wss.clients.size} clients`);
      }
    } catch (error) {
      console.error('Failed to broadcast WebSocket message:', error);
    }
  }

  private async initializeClient() {
    if (this.isInitializing) {
      console.log('âš ï¸ Client already initializing, waiting for completion...');
      // Wait for current initialization to complete or timeout
      let attempts = 0;
      while (this.isInitializing && attempts < 10) {
        await new Promise(resolve => setTimeout(resolve, 1000));
        attempts++;
      }

      if (this.isInitializing) {
        console.log('ðŸ”„ Force resetting initialization flag after timeout');
        this.isInitializing = false;
      }
    }

    // Additional safety check for existing client
    if (this.client) {
      try {
        await this.client.destroy();
        console.log('ðŸ§¹ Destroyed existing client before reinitializing');
      } catch (e: any) {
        console.log('Previous client cleanup:', e.message);
      }
      this.client = null;
    }

    try {
      this.isInitializing = true;
      console.log('ðŸš€ Initializing WhatsApp client...');

      // Check if there's an existing session
      const fs = await import('fs');
      const path = await import('path');
      const sessionPath = path.resolve('./.wwebjs_auth');
      const hasExistingSession = fs.existsSync(sessionPath);

      // Also check for stored session info in database
      let storedSessionInfo = null;
      try {
        const activeSessions = await storage.getActiveSessions();
        if (activeSessions.length > 0) {
          storedSessionInfo = activeSessions[0];
          console.log('ðŸ“¦ Found stored session info:', storedSessionInfo.userId);
        }
      } catch (error: any) {
        console.log('Session info retrieval failed:', error.message);
      }

      if (hasExistingSession) {
        console.log('ðŸ” Found existing session files, attempting automatic restoration...');

        // If we have stored session info, use it for immediate UI updates
        if (storedSessionInfo) {
          console.log('ðŸ“¦ Restoring from stored session info for UI');
          this.sessionInfo = {
            number: storedSessionInfo.userId,
            name: storedSessionInfo.userName,
            loginTime: storedSessionInfo.loginTime
          };
        } else {
          console.log('ðŸ“‹ Session files exist, will restore on WhatsApp ready event');
        }

        // Don't mark as ready until client is actually connected and ready
        this.isReady = false;
      } else {
        console.log('ðŸ“± No existing session found, will require QR authentication');
      }

      // Clean up existing client
      if (this.client) {
        try {
          await this.client.destroy();
        } catch (e: any) {
          console.log('Old client cleanup (expected):', e.message);
        }
        this.client = null;
      }

      // Reset state but preserve session info if we're restoring
      this.qrCode = null;
      if (!hasExistingSession && !storedSessionInfo) {
        this.sessionInfo = null;
        this.isReady = false;
      }
      this.messageCache.clear(); // Clear message cache on reinitialize

      // Use full puppeteer with proper configuration to fix execution context issues
      this.client = new Client({
        authStrategy: new LocalAuth({
          clientId: "main_session", // Persistent session ID for session preservation
          dataPath: "./.wwebjs_auth" // Explicit data path
        }),
        puppeteer: {
          headless: true,
          args: [
            '--no-sandbox',
            '--disable-setuid-sandbox',
            '--disable-dev-shm-usage',
            '--disable-accelerated-2d-canvas',
            '--no-first-run',
            '--no-zygote',
            '--disable-gpu',
            '--disable-background-timer-throttling',
            '--disable-backgrounding-occluded-windows',
            '--disable-renderer-backgrounding',
            '--disable-web-security',
            '--disable-features=VizDisplayCompositor',
            '--disable-ipc-flooding-protection',
            '--memory-pressure-off',
            '--max_old_space_size=4096',
            '--disable-extensions',
            '--disable-plugins',
            '--disable-sync',
            '--disable-translate',
            '--hide-scrollbars',
            '--mute-audio',
            '--no-default-browser-check',
            '--no-pings',
            '--disable-default-apps',
            '--disable-component-extensions-with-background-pages',
            '--force-single-process-tabs',
            '--single-process',
            '--disable-process-per-site',
            '--disable-site-isolation-trials',
            `--user-data-dir=./.chrome_user_data_${Date.now()}` // Use unique directory to avoid conflicts
          ],
          handleSIGINT: false,
          handleSIGTERM: false,
          handleSIGHUP: false,
          timeout: 90000 // 90 second timeout for initialization
        }
      });

      // Event handlers
      this.client.on('qr', (qr: string) => {
        console.log('ðŸ“± New QR Code received from WhatsApp Web');
        console.log('âš ï¸ Note: QR code means session was not restored or has expired');
        console.log('ðŸ” QR String type:', typeof qr);
        console.log('ðŸ” QR String length:', qr.length);
        console.log('ðŸ” QR String preview:', qr.substring(0, 120) + '...');

        try {
          // Fix: qr-image returns a stream, we need to convert it properly
          const qrStream = qrImage.image(qr, { type: 'png' });
          const chunks: Buffer[] = [];

          qrStream.on('data', (chunk: Buffer) => chunks.push(chunk));
          qrStream.on('end', () => {
            const qrBuffer = Buffer.concat(chunks);
            const qrDataURL = 'data:image/png;base64,' + qrBuffer.toString('base64');

            this.qrCode = qrDataURL;
            console.log('âœ… QR Code generated successfully with qr-image');

            // Broadcast QR code to all connected WebSocket clients
            this.broadcastToClients('qr', { qr: qrDataURL });
          });

          qrStream.on('error', (error: any) => {
            console.error('âŒ QR stream error:', error.message);
            this.qrCode = null;
          });

        } catch (qrError: any) {
          console.error('âŒ QR generation with qr-image failed:', qrError.message);

          // Fallback: Try with QRCode library
          QRCode.toDataURL(qr)
            .then((qrDataURL: string) => {
              this.qrCode = qrDataURL;
              console.log('âœ… QR Code generated successfully with fallback method');

              // Broadcast QR code to all connected WebSocket clients
              this.broadcastToClients('qr', { qr: qrDataURL });
            })
            .catch((fallbackError: any) => {
              console.error('âŒ Fallback QR generation also failed:', fallbackError.message);
              this.qrCode = null;
            });
        }
      });

      this.client.on('ready', async () => {
        console.log('âœ… WhatsApp client is ready - session restored successfully!');
        this.isReady = true;

        // Update session info with fresh data from client
        const freshSessionInfo = {
          number: this.client.info?.wid?.user || this.sessionInfo?.number || 'unknown',
          name: this.client.info?.pushname || this.sessionInfo?.name || 'unknown',
          loginTime: this.sessionInfo?.loginTime || new Date().toISOString()
        };

        this.sessionInfo = freshSessionInfo;

        // Clear QR code since we're now authenticated
        this.qrCode = null;

        console.log('ðŸŽ‰ Session restored automatically! User:', freshSessionInfo.name, 'Number:', freshSessionInfo.number);

        // Save/update session info to storage for future persistence
        try {
          // First clear any old sessions
          await storage.clearAllSessions();

          // Create comprehensive session backup
          const sessionBackup = {
            sessionId: "main_session",
            userId: freshSessionInfo.number,
            userName: freshSessionInfo.name,
            timestamp: new Date().toISOString(),
            status: "active"
          };

          // Save to backup file
          const fs = await import('fs');
          await fs.promises.writeFile('.session_backup.json', JSON.stringify(sessionBackup, null, 2));
          console.log('ðŸ’¾ Session backup saved to file');

          // Save the current active session
          await storage.saveSession({
            userId: freshSessionInfo.number,
            userName: freshSessionInfo.name,
            loginTime: new Date(freshSessionInfo.loginTime),
            sessionData: JSON.stringify(freshSessionInfo)
          });
          console.log('ðŸ’¾ Session info saved to storage for future persistence');

          // Create a backup marker file to indicate successful session
          try {
            const fs = await import('fs');
            const sessionMarker = {
              sessionId: 'main_session',
              userId: freshSessionInfo.number,
              userName: freshSessionInfo.name,
              timestamp: new Date().toISOString(),
              status: 'active'
            };
            fs.writeFileSync('./.session_backup.json', JSON.stringify(sessionMarker, null, 2));
            console.log('ðŸ“„ Session backup marker created');
          } catch (fsError: any) {
            console.log('Session marker creation failed:', fsError.message);
          }
        } catch (error: any) {
          console.log('Session save failed (non-critical):', error.message);
        }

        // Broadcast connection status immediately
        this.broadcastToClients('connected', { 
          connected: true, 
          sessionInfo: this.sessionInfo 
        });

        // Full data synchronization after client is ready
        setTimeout(() => {
          this.performFullDataSync();
        }, 2000); // Wait 2 seconds to ensure client is fully ready
      });

      this.client.on('authenticated', () => {
        console.log('ðŸ” WhatsApp client authenticated successfully');
        console.log('âœ… Session restoration successful - no QR code needed');
      });

      this.client.on('auth_failure', (msg: any) => {
        console.error('âŒ Authentication failed:', msg);
        this.isReady = false;
        this.sessionInfo = null;
      });

      this.client.on('disconnected', (reason: any) => {
        console.log('ðŸ”Œ WhatsApp client disconnected:', reason);
        this.isReady = false;

        // Clear session info on logout/UNPAIRED - critical for real-time updates
        if (reason === 'UNPAIRED' || reason === 'LOGOUT') {
          console.log('ðŸ“± User logged out from phone - clearing session data');
          this.sessionInfo = null;
          this.clearStoredSession();
        }
        this.qrCode = null;

        // Broadcast disconnection with detailed reason
        this.broadcastToClients('disconnected', { 
          connected: false, 
          reason,
          requiresNewAuth: reason === 'UNPAIRED' || reason === 'LOGOUT'
        });

        // If user logged out from phone, restart with QR immediately
        if (reason === 'UNPAIRED' || reason === 'LOGOUT') {
          console.log('ðŸ”„ Phone logout detected - restarting for new QR');
          this.handlePhoneLogoutRestart();
        } else {
          console.log('ðŸ“‹ Use /api/reconnect-whatsapp to attempt reconnection with preserved session');
          console.log('ðŸ“‹ Use /api/force-restart-whatsapp to start fresh with QR code');
        }
      });

      // Real-time message handling
      this.client.on('message', (message: any) => {
        this.storeRealtimeMessage(message);
      });

      // Listen for outgoing messages sent from phone
      this.client.on('message_create', (message: any) => {
        if (message.fromMe) {
          console.log('ðŸ“± Outgoing message from phone detected');
          this.storeRealtimeMessage(message);
        }
      });

      console.log('âœ… Starting client initialization...');
      try {
        await this.client.initialize();
        console.log('ðŸŽ¯ Client initialization completed successfully');
      } catch (initError: any) {
        console.error('âŒ Client initialization failed:', initError.message);

        // If initialization fails, reset and try again after delay
        this.isInitializing = false;
        this.client = null;

        setTimeout(() => {
          console.log('ðŸ”„ Retrying client initialization after failure...');
          this.initializeClient();
        }, 5000);

        throw initError;
      }

    } catch (error: any) {
      console.error('âŒ WhatsApp client initialization failed:', error.message);
      console.error('Error details:', error);
      this.isReady = false;
      this.sessionInfo = null;
      this.qrCode = null;

      // Handle specific error types
      if (error.message.includes('Protocol error') || 
          error.message.includes('Target closed') ||
          error.message.includes('Navigation timeout')) {
        console.log('ðŸ”§ Protocol/Connection error handled - attempting clean restart');
        // Clean restart for protocol errors
        setTimeout(() => {
          this.initializeClient();
        }, 5000);
      } else {
        // Attempt retry after a delay for other transient network issues
        console.log('ðŸ”„ Scheduling retry in 10 seconds...');
        setTimeout(() => {
          if (!this.isReady && !this.isInitializing) {
            console.log('ðŸ”„ Retrying WhatsApp client initialization...');
            this.initializeClient();
          }
        }, 10000);
      }

      console.log('Browser failed to initialize - QR will be available when browser starts');
    } finally {
      this.isInitializing = false;
    }
  }

