  // Public method to trigger data sync manually
  async triggerDataSync() {
    if (!this.isReady) {
      throw new Error('WhatsApp client not ready for sync');
    }
    return this.performFullDataSync();
  }
}

export const whatsappService = new WhatsAppService();

