client/src/components/ui/navigation.tsx.content:
import React from "react";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";

interface NavigationProps {
  showAuthButtons?: boolean;
  currentPage?: 'home' | 'login' | 'signup' | 'features' | 'pricing' | 'blogs';
}

const Navigation: React.FC<NavigationProps> = ({ showAuthButtons = true, currentPage }) => {
  const [location] = useLocation();

  const isCurrentPage = (page: string) => {
    if (currentPage) return currentPage === page;
    return location === `/${page}` || (page === 'home' && location === '/');
  };

  const linkClass = (page: string) => {
    return isCurrentPage(page) 
      ? "text-gray-400 dark:text-gray-500 cursor-not-allowed transition-colors"
      : "text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white transition-colors cursor-pointer";
  };

  const NavLink = ({ href, page, children }: { href: string; page: string; children: React.ReactNode }) => {
    if (isCurrentPage(page)) {
      return <span className={linkClass(page)}>{children}</span>;
    }
    return <Link href={href} className={linkClass(page)}>{children}</Link>;
  };

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-white/80 dark:bg-gray-900/80 backdrop-blur-md border-b border-gray-200 dark:border-gray-700">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <div className="flex items-center space-x-2">
            <Link href="/">
              <img src="/hw-logo.png" alt="HubWale" className="h-8 w-auto cursor-pointer" loading="lazy" />
            </Link>
          </div>

          {/* Navigation Links */}
          <div className="hidden md:flex items-center space-x-8">
            <NavLink href="/" page="home">Home</NavLink>
            <NavLink href="/features" page="features">Features</NavLink>
            <NavLink href="/pricing" page="pricing">Pricing</NavLink>
            <NavLink href="/blogs" page="blogs">Blogs</NavLink>
          </div>

          {/* Auth Buttons */}
          {showAuthButtons && (
            <div className="flex items-center space-x-4">
              {isCurrentPage('login') ? (
                <Button 
                  variant="ghost" 
                  className="text-gray-400 dark:text-gray-500 cursor-not-allowed"
                  disabled
                >
                  Login
                </Button>
              ) : (
                <Link href="/login">
                  <Button variant="ghost" className="text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white">
                    Login
                  </Button>
                </Link>
              )}

              {isCurrentPage('signup') ? (
                <Button 
                  className="bg-gray-400 hover:bg-gray-400 text-white shadow-lg cursor-not-allowed"
                  disabled
                >
                  Sign up
                </Button>
              ) : (
                <Link href="/signup">
                  <Button className="bg-gradient-to-r from-orange-500 to-red-500 hover:from-orange-600 hover:to-red-600 text-white shadow-lg">
                    Sign up
                  </Button>
                </Link>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navigation;

server/services/whatsapp_broken.ts.content:
import pkg from 'whatsapp-web.js';
const { Client, LocalAuth, MessageMedia } = pkg as any;
import QRCode from 'qrcode';
import qrImage from 'qr-image';
import { storage } from '../storage';
import fs from 'fs';
import path from 'path';
import type { WebSocket } from 'ws';

export class WhatsAppService {
  private client: any = null;
  private qrCode: string | null = null;
  private sessionInfo: any = null;
  private isReady: boolean = false;
  private isInitializing: boolean = false;
  private messageCache: Map<string, any[]> = new Map(); // Cache for real-time messages

  constructor() {
    this.initializeClient();
  }

  // Helper method to broadcast WebSocket events
  private broadcastToClients(eventType: string, data: any) {
    try {
      const wss = (global as any).wss;
      if (wss && wss.clients) {
        const message = JSON.stringify({ type: eventType, data });
        wss.clients.forEach((client: any) => {
          if (client.readyState === 1) { // WebSocket.OPEN
            client.send(message);
          }
        });
        console.log(`ðŸ“¡ Broadcasted ${eventType} to ${wss.clients.size} clients`);
      }
    } catch (error) {
      console.error('Failed to broadcast WebSocket message:', error);
    }
  }

  private async initializeClient() {
    if (this.isInitializing) {
      console.log('Client already initializing, skipping...');
      return;
    }

    try {
      this.isInitializing = true;
      console.log('ðŸš€ Initializing WhatsApp client...');

      // Clean up existing client
      if (this.client) {
        try {
          await this.client.destroy();
        } catch (e: any) {
          console.log('Old client cleanup (expected):', e.message);
        }
        this.client = null;
      }

      // Reset all state
      this.qrCode = null;
      this.sessionInfo = null;
      this.isReady = false;
      this.messageCache.clear(); // Clear message cache on reinitialize

      // Use full puppeteer with proper configuration to fix execution context issues
      this.client = new Client({
        authStrategy: new LocalAuth({
          clientId: "main_session", // Persistent session ID for session preservation
          dataPath: "./.wwebjs_auth" // Explicit data path
        }),
        puppeteer: {
          headless: true, // Must be headless in cloud environment
          slowMo: 100, // Slow down operations for stability
          executablePath: '/nix/store/qa9cnw4v5xkxyip6mb9kxqfq1z4x2dx1-chromium-138.0.7204.100/bin/chromium',
          args: [
            '--no-sandbox',
            '--disable-setuid-sandbox',
            '--disable-dev-shm-usage',
            '--disable-accelerated-2d-canvas',
            '--no-first-run',
            '--no-zygote',
            '--disable-gpu',
            '--disable-web-security',
            '--disable-features=VizDisplayCompositor',
            '--disable-background-timer-throttling',
            '--disable-backgrounding-occluded-windows',
            '--disable-renderer-backgrounding',
            '--disable-blink-features=AutomationControlled',
            '--disable-extensions',
            '--disable-ipc-flooding-protection',
            `--user-data-dir=/tmp/chrome-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
            '--disable-session-crashed-bubble',
            '--disable-infobars',
            '--force-no-sandbox',
            '--remote-debugging-port=0'
          ],
        },
      });

      this.setupEventHandlers();

      console.log('âœ… Starting client initialization...');
      await this.client.initialize();
    } catch (error) {
      console.error('âŒ Failed to initialize WhatsApp client:', error);
      this.isInitializing = false;
      this.client = null;

      console.log('Browser failed to initialize - QR will be available when browser starts');

      // Retry initialization after delay
      setTimeout(() => {
        console.log('ðŸ”„ Retrying client initialization...');
        this.initializeClient();
      }, 3000);
    }
  }

  private setupEventHandlers() {
    if (!this.client) return;

    this.client.on('qr', async (qrString: string) => {
      console.log('ðŸ“± New QR Code received from WhatsApp Web');
      console.log('ðŸ” QR String type:', typeof qrString);
      console.log('ðŸ” QR String length:', qrString.length);
      console.log('ðŸ” QR String preview:', qrString.substring(0, 100) + '...');

      // Check if qrString is already a base64 data URL (WhatsApp Web sometimes provides this)
      if (qrString.startsWith('data:image/')) {
        console.log('âœ… QR Code already in base64 format from WhatsApp Web');
        this.qrCode = qrString;
        this.broadcastToClients('qr', { qr: this.qrCode });
      } else {
        // Try to generate QR code from the string
        try {
          // Use qr-image library directly since it handles large data better
          const qrBuffer = qrImage.imageSync(qrString, { 
            type: 'png', 
            size: 20, // Larger size for better scanning
            margin: 2,
            ec_level: 'L' // Low error correction for maximum data capacity
          });
          this.qrCode = `data:image/png;base64,${qrBuffer.toString('base64')}`;
          console.log('âœ… QR Code generated successfully with qr-image');
          this.broadcastToClients('qr', { qr: this.qrCode });
        } catch (err) {
          console.error('QR image generation failed:', err);
          // Last resort: store the raw string and let client handle it
          this.qrCode = qrString;
          this.broadcastToClients('qr', { qr: null, rawQr: qrString });
        }
      }
      this.sessionInfo = null;
    });

    this.client.on('ready', async () => {
      console.log('WhatsApp client is ready');
      this.isReady = true;
      this.isInitializing = false;
      this.qrCode = null;

      // Broadcast session connected event
      this.broadcastToClients('connected', { connected: true });

      // Multiple attempts to get user info with different delays
      const getUserInfo = async (attempt = 1) => {
        try {
          if (this.client && this.client.info) {
            const info = this.client.info;
            const userName = info.pushname || info.wid?.user || "Unknown User";

            this.sessionInfo = {
              name: userName,
              loginTime: this.sessionInfo?.loginTime || new Date().toISOString(),
              isFirstConnection: false // Existing session, not first connection
            };

            await storage.clearAllSessions();
            await storage.createSession({
              userId: info.wid?.user || 'unknown',
              userName: userName,
              loginTime: new Date(),
              sessionData: JSON.stringify({ wid: info.wid }),
            });
            console.log('âœ… Session saved for user:', userName);
            this.qrCode = null;
            return;
          }

          // Fallback to use session-based name
          const userName = "Connected User";
          this.sessionInfo = {
            name: userName,
            loginTime: this.sessionInfo?.loginTime || new Date().toISOString(),
            isFirstConnection: false // Existing session, not first connection
          };

          await storage.clearAllSessions();
          await storage.createSession({
            userId: 'fallback-user',
            userName: userName,
            loginTime: new Date(),
            sessionData: JSON.stringify({ fallback: true }),
          });
          console.log('âœ… Session saved for user:', userName);
          this.qrCode = null;
        } catch (error: any) {
          console.error(`âš ï¸ Attempt ${attempt} - Error loading user info:`, error.message);

          // Retry with increasing delays, max 3 attempts
          if (attempt < 3) {
            setTimeout(() => getUserInfo(attempt + 1), attempt * 2000);
          } else {
            // Fallback to authenticated user without detailed info
            this.sessionInfo = {
              name: "WhatsApp User",
              loginTime: this.sessionInfo?.loginTime || new Date().toISOString(),
              isFirstConnection: false // Fallback session, not first connection
            };
            console.log('Using fallback user info');
          }
        }
      };

      // Start first attempt after 2 seconds
      setTimeout(() => getUserInfo(1), 2000);
    });

    this.client.on('authenticated', () => {
      console.log('WhatsApp client authenticated');
      this.qrCode = null;
      this.sessionInfo = {
        name: "Fetching...",
        loginTime: new Date().toISOString(),
        isFirstConnection: true // Mark as first connection for auto-redirect
      };
    });

    this.client.on('auth_failure', (msg: any) => {
      console.error('WhatsApp authentication failed:', msg);
      this.qrCode = null;
      this.isReady = false;
    });

    this.client.on('disconnected', (reason: any) => {
      console.log('ðŸ”Œ Client disconnected:', reason);
      this.isReady = false;
      this.qrCode = null;
      this.sessionInfo = null;
      this.messageCache.clear(); // Clear cached messages on disconnect
      storage.clearAllSessions();

      // Broadcast disconnection event
      this.broadcastToClients('disconnected', { connected: false });

      // Reinitialize client on disconnect with delay
      setTimeout(() => {
        this.initializeClient();
      }, 2000);
    });

    // Listen for incoming messages in real-time
    this.client.on('message', async (message: any) => {
      try {
        console.log('ðŸ“¥ New message received:', {
          from: message.from,
          body: message.body || '[Media]',
          timestamp: message.timestamp,
          fromMe: message.fromMe
        });

        // Store the message in memory for immediate retrieval
        await this.storeRealtimeMessage(message);
      } catch (error: any) {
        console.error('Error handling incoming message:', error.message);
      }
    });

    // Listen for message acks (delivered, read, etc.)
    this.client.on('message_ack', (msg: any, ack: any) => {
      console.log('ðŸ“§ Message ACK received:', {
        messageId: msg.id?._serialized,
        ack: ack // 1: sent, 2: received, 3: read, 4: played
      });
    });

    // Listen for group join events
    this.client.on('group_join', (notification: any) => {
      console.log('ðŸ‘¥ Group join event:', notification);
    });

    // Listen for group leave events  
    this.client.on('group_leave', (notification: any) => {
      console.log('ðŸ‘‹ Group leave event:', notification);
    });

    // Listen for remote session logout (when user logs out from phone)
    this.client.on('remote_session_saved', () => {
      console.log('ðŸ“± Remote session saved');
    });

    // Listen for logout events from phone
    this.client.on('logout', () => {
      console.log('ðŸ“± Logout detected from phone');
      this.handleLogout();
    });

    // Listen for authentication failure (also triggered by logout from phone)
    this.client.on('auth_failure', (message: any) => {
      console.log('ðŸ” Authentication failure:', message);
      if (message === 'LOGOUT') {
        this.handleLogout();
      }
    });
